// components/Intro.jsx
import { motion } from "framer-motion";
import Typewriter from "typewriter-effect";

const Intro = () => {
  return (
    <section className="min-h-screen bg-gradient-to-b from-black via-gray-900 to-black flex flex-col items-center justify-center text-white px-6">
      {/* Main Heading */}
      <motion.h1
        className="text-5xl sm:text-6xl md:text-7xl font-bold text-center mb-4"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        Hi, I'm <span className="text-blue-500">Himanshu</span>
      </motion.h1>

      {/* Typewriter Effect */}
      <motion.div
        className="text-xl sm:text-2xl text-center text-gray-300 mb-8"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1, duration: 1 }}
      >
        <Typewriter
          options={{
            strings: [
              "Full Stack Developer ðŸ’»",
              "Creative Designer ðŸŽ¨",
              "Tech Enthusiast ðŸš€",
              "Problem Solver ðŸ§ ",
            ],
            autoStart: true,
            loop: true,
            delay: 60,
            deleteSpeed: 30,
          }}
        />
      </motion.div>

      {/* Description */}
      <motion.p
        className="max-w-xl text-center text-gray-400 mb-10 leading-relaxed"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 1.5, duration: 1 }}
      >
        I'm passionate about building modern web experiences with elegant UI, smooth animations, and seamless functionality. Let's build something amazing together.
      </motion.p>

      {/* Call to Action Button */}
      <motion.a
        href="#Contact"
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.95 }}
        className="bg-blue-600 hover:bg-blue-500 text-white px-6 py-3 rounded-lg text-lg font-semibold shadow-lg hover:shadow-blue-600 transition duration-300"
      >
        Let's Talk âœ¨
      </motion.a>
    </section>
  );
};

export default Intro;



























