import { motion } from "framer-motion";

const Skills = () => {
  const categories = [
    {
      title: "Frontend Development",
      skills: ["HTML5", "CSS3", "JavaScript", "React.js", "Tailwind CSS", "Next.js"],
    },
    {
      title: "Backend Development",
      skills: ["Node.js", "Express.js", "MongoDB", "Firebase", "SQL"],
    },
    {
      title: "Programming & Tools",
      skills: ["Python", "C/C++", "Git & GitHub", "VS Code", "Figma"],
    },
    {
      title: "Others",
      skills: ["REST API", "Responsive Design", "Web Accessibility", "Problem Solving"],
    },
  ];

  return (
    <motion.section
      id="skills"
      className="min-h-screen py-20 px-6 bg-gradient-to-br from-[#0f172a] to-[#1e293b] text-white flex flex-col justify-center items-center"
      initial={{ opacity: 0, y: 40 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 1 }}
      viewport={{ once: true }}
    >
      <motion.h2
        className="text-4xl font-bold text-center mb-16 text-blue-400 underline decoration-blue-500 decoration-4"
        initial={{ opacity: 0, y: -20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        My Skills
      </motion.h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-10 max-w-6xl w-full">
        {categories.map((category, index) => (
          <motion.div
            key={index}
            className="bg-[#1e293b] rounded-2xl p-8 border border-blue-900 shadow-lg hover:shadow-blue-800/40 transition duration-300"
            whileHover={{ scale: 1.03 }}
          >
            <h3 className="text-2xl font-semibold text-blue-300 mb-6 text-center">
              {category.title}
            </h3>
            <ul className="grid grid-cols-2 sm:grid-cols-3 gap-4">
              {category.skills.map((skill, idx) => (
                <motion.li
                  key={idx}
                  className="bg-blue-700 text-white text-sm sm:text-base px-4 py-2 rounded-lg shadow-md text-center hover:bg-blue-500 transition-all duration-200"
                  whileHover={{ scale: 1.1 }}
                >
                  {skill}
                </motion.li>
              ))}
            </ul>
          </motion.div>
        ))}
      </div>
    </motion.section>
  );
};

export default Skills;
