import { motion } from "framer-motion";

const Skills = () => {
  const categories = [
    {
      title: "Frontend Development",
      skills: ["HTML5", "CSS3", "JavaScript", "React.js", "Tailwind CSS", "Next.js"],
    },
    {
      title: "Backend Development",
      skills: ["Node.js", "Express.js", "MongoDB", "Firebase", "SQL"],
    },
    {
      title: "Programming & Tools",
      skills: ["Python", "C/C++", "Git & GitHub", "VS Code", "Figma"],
    },
    {
      title: "Others",
      skills: ["REST API", "Responsive Design", "Web Accessibility", "Problem Solving"],
    },
  ];

  return (
    <motion.section
      id="skills"
      className="min-h-screen py-20 px-6 bg-[#0a0a0a] text-white flex flex-col justify-center items-center"
      initial={{ opacity: 0, y: 40 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 1 }}
      viewport={{ once: true }}
    >
      <motion.h2
        initial={{ opacity: 0, y: -30 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="text-4xl font-bold mb-12 text-blue-400 underline decoration-blue-500 decoration-2 text-center"
      >
        My Skills
      </motion.h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-10 w-full max-w-6xl">
        {categories.map((category, index) => (
          <motion.div
            key={index}
            className="bg-[#121212] rounded-xl border border-blue-900 shadow-lg p-8 transition-transform duration-300 hover:scale-105 hover:shadow-blue-500/30"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: index * 0.2 }}
            viewport={{ once: true }}
          >
            <h3 className="text-2xl font-semibold text-blue-300 mb-6">{category.title}</h3>
            <ul className="grid grid-cols-2 gap-4">
              {category.skills.map((skill, idx) => (
                <motion.li
                  key={idx}
                  whileHover={{ scale: 1.08 }}
                  className="bg-blue-800 text-white text-sm font-medium px-4 py-2 rounded-md shadow-md hover:bg-blue-600 hover:shadow-blue-400/40 transition-all duration-200 text-center"
                >
                  {skill}
                </motion.li>
              ))}
            </ul>
          </motion.div>
        ))}
      </div>
    </motion.section>
  );
};

export default Skills;
