import { motion } from "framer-motion";
import Typewriter from "typewriter-effect";

const Intro = () => {
  return (
    <section
      id="intro"
      className="relative h-screen flex flex-col justify-center items-center bg-black text-white px-6 overflow-hidden"
    >
      {/* Floating Gradient Blobs */}
      <motion.div
        className="absolute w-72 h-72 opacity-20 rounded-full blur-3xl top-[-100px] left-[-100px] z-0"
        animate={{ scale: [1, 1.4, 1], opacity: [0.2, 0.3, 0.2] }}
        transition={{ repeat: Infinity, duration: 10 }}
      />
      <motion.div
        className="absolute w-96 h-96 opacity-20 rounded-full blur-3xl bottom-[-120px] right-[-120px] z-0"
        animate={{ scale: [1, 1.3, 1], opacity: [0.2, 0.4, 0.2] }}
        transition={{ repeat: Infinity, duration: 12 }}
      />

      {/* Heading */}
      <motion.h1
        initial={{ opacity: 0, y: -80 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
        viewport={{ once: true }}
        className="text-4xl sm:text-6xl font-extrabold text-center mb-6 z-10 drop-shadow-lg"
      >
        Hi, I'm{" "}
        <span className="text-blue-500 animate-pulse">Himanshu Kumar</span>
      </motion.h1>

      {/* Typewriter Effect */}
      <motion.div
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        transition={{ delay: 0.6, duration: 1 }}
        viewport={{ once: true }}
        className="text-xl sm:text-2xl font-medium text-center mb-4 z-10"
      >
        <Typewriter
          options={{
            strings: [
              "Full Stack Developer",
              "Software Developer",
              "Leadership",
              "Tech Explorer",
              "Open Source Contributor",
            ],
            autoStart: true,
            loop: true,
          }}
        />
      </motion.div>

      {/* Description */}
      <motion.p
        initial={{ opacity: 0, y: 40 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ delay: 1.2, duration: 1 }}
        viewport={{ once: true }}
        className="max-w-2xl text-center text-gray-300 mb-8 leading-relaxed z-10 text-lg"
      >
        I design and build modern, performant web applications with stunning UIs,
        seamless UX, and clean code. Letâ€™s create something exceptional together!
      </motion.p>

      {/* Buttons with scroll animation + glowing effect */}
      <motion.div
        initial={{ opacity: 0, scale: 0.9 }}
        whileInView={{ opacity: 1, scale: 1 }}
        transition={{ delay: 1.8, duration: 0.6 }}
        viewport={{ once: true }}
        className="flex gap-8 z-10"
      >
        <a
          href="#projects"
          className="px-7 py-3 bg-blue-600 text-white font-semibold rounded-lg shadow-lg transition-all duration-300 hover:shadow-blue-500 hover:scale-105 relative overflow-hidden group"
        >
          <span className="relative z-10">View Projects</span>
          <span className="absolute inset-0 bg-blue-400 blur-xl opacity-30 group-hover:opacity-100 transition-all duration-500"></span>
        </a>
        <a
          href="#contact"
          className="px-7 py-3 border border-white text-white font-semibold rounded-lg transition-all duration-300 hover:bg-white hover:text-black hover:scale-105 relative overflow-hidden group"
        >
          <span className="relative z-10">Contact Me</span>
          <span className="absolute inset-0 bg-white blur-xl opacity-10 group-hover:opacity-40 transition-all duration-500"></span>
        </a>
      </motion.div>
    </section>
  );
};

export default Intro;
