import { motion } from "framer-motion";
import Typewriter from "typewriter-effect";
import profilePic from "../assets/profile.jpg"; // Update the path as needed

const Intro = () => {
  return (
    <section
      id="intro"
      className="min-h-screen bg-black text-white flex flex-col md:flex-row items-center justify-between px-10 py-20"
    >
      {/* Left Side */}
      <div className="md:w-1/2 w-full flex flex-col items-start justify-center space-y-6">
        <motion.h1
          initial={{ opacity: 0, y: -20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-4xl md:text-5xl font-bold"
        >
          Hi, I'm Himanshu Kumar
        </motion.h1>

        <motion.div
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ delay: 0.2, duration: 0.8 }}
          className="text-lg text-gray-300"
        >
          <Typewriter
            options={{
              strings: ["Full Stack Developer", "Innovator", "Tech Enthusiast"],
              autoStart: true,
              loop: true,
            }}
          />
        </motion.div>

        <div className="flex space-x-4">
          <a
            href="/CV_Himanshu.pdf"
            className="bg-blue-600 hover:bg-blue-700 transition px-6 py-2 rounded-lg font-medium"
            download
          >
            Download CV
          </a>
          <a
            href="#projects"
            className="border border-blue-500 hover:bg-blue-600 transition px-6 py-2 rounded-lg font-medium"
          >
            My Projects
          </a>
        </div>
      </div>

      {/* Right Side */}
      <div className="md:w-1/2 w-full flex flex-col items-center justify-center mt-10 md:mt-0 space-y-8">
        {/* Floating Profile Pic */}
        <motion.div
          animate={{
            y: [0, -10, 0],
          }}
          transition={{
            duration: 3,
            repeat: Infinity,
            ease: "easeInOut",
          }}
          className="relative rounded-full border-4 border-blue-500 p-2 hover:shadow-[0_0_25px_5px_rgba(59,130,246,0.7)] transition-all"
        >
          <img
            src={profilePic}
            alt="Himanshu Kumar"
            className="w-40 h-40 object-cover rounded-full"
          />
        </motion.div>

        {/* Dual Section Cards */}
        <div className="flex flex-col md:flex-row gap-6">
          {/* Software Developer */}
          <motion.div
            whileHover={{ scale: 1.05 }}
            className="bg-gray-800 p-6 rounded-lg shadow-lg w-64"
          >
            <h3 className="text-xl font-bold mb-2 text-blue-400">Software Developer</h3>
            <p className="text-gray-300 text-sm">
              Experienced in full-stack development using React, Node.js, and MongoDB.
              Passionate about writing clean, efficient code.
            </p>
          </motion.div>

          {/* Entrepreneur */}
          <motion.div
            whileHover={{ scale: 1.05 }}
            className="bg-gray-800 p-6 rounded-lg shadow-lg w-64"
          >
            <h3 className="text-xl font-bold mb-2 text-green-400">Entrepreneur</h3>
            <p className="text-gray-300 text-sm">
              Founder of innovative tech solutions. Focused on solving real-world problems
              through scalable digital products.
            </p>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default Intro;
