import { motion } from "framer-motion";

const Navbar = ({ setPage }) => {
  const items = ["Intro", "Skills", "Projects", "Achievements", "Contact"];

  return (
    <nav className="fixed top-0 left-0 w-full bg-black py-4 px-6 flex justify-center gap-4 z-50 shadow-md">
      {items.map((item, idx) => (
        <motion.button
          key={idx}
          whileHover={{ scale: 1.1, color: "#3b82f6" }}
          className="text-white hover:text-blue-500 font-medium transition-colors"

          onClick={() => setPage(item)}
        >
          {item}
        </motion.button>
      ))}
    </nav>
  );
};

export default Navbar;
