import { motion } from "framer-motion";

const About = () => {
  return (
    <section
      id="about"
      className="min-h-screen flex flex-col md:flex-row justify-center items-center bg-gradient-to-b from-black to-gray-900 text-white py-20 px-6"
    >
      {/* Left Side: About Me Text */}
      <div className="md:w-1/2 w-full flex flex-col justify-center items-start space-y-6">
        <motion.h2
          initial={{ opacity: 0, y: -20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-4xl font-bold mb-4"
        >
          About Me
        </motion.h2>

        <motion.p
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ delay: 0.3, duration: 0.8 }}
          className="text-lg leading-relaxed text-gray-300"
        >
          I'm Himanshu Kumar, a passionate Full Stack Developer with a love for building
          elegant and functional digital experiences. From frontend aesthetics to backend
          logic, I bring projects to life using modern tools and clean code. I enjoy exploring
          new technologies, contributing to open source, and leading impactful software development.
          Let's build something extraordinary together!
        </motion.p>
      </div>

      {/* Right Side: Developer and Entrepreneur Cards */}
      <div className="md:w-1/2 w-full mt-10 md:mt-0 flex flex-col items-center justify-center gap-6">
        <motion.div
          whileHover={{ scale: 1.05 }}
          className="bg-gray-800 p-6 rounded-lg shadow-lg w-72 md:w-80"
        >
          <h3 className="text-xl font-bold mb-2 text-blue-400">Software Developer</h3>
          <p className="text-gray-300 text-sm">
            Skilled in full-stack development using React, Node.js, Express, and MongoDB.
            I build high-performance, responsive, and maintainable web applications.
          </p>
        </motion.div>

        <motion.div
          whileHover={{ scale: 1.05 }}
          className="bg-gray-800 p-6 rounded-lg shadow-lg w-72 md:w-80"
        >
          <h3 className="text-xl font-bold mb-2 text-green-400">Entrepreneur</h3>
          <p className="text-gray-300 text-sm">
            Founder of innovative solutions that solve real-world problems.
            I build scalable tech products and aim to empower users through thoughtful design and tech.
          </p>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
