import { motion } from "framer-motion";

const Skills = () => {
  const categories = [
    {
      title: "Frontend Development",
      skills: ["HTML5", "CSS3", "JavaScript", "React.js", "Tailwind CSS", "Next.js"],
    },
    {
      title: "Backend Development",
      skills: ["Node.js", "Express.js", "MongoDB", "Firebase", "SQL"],
    },
    {
      title: "Programming & Tools",
      skills: ["Python", "C/C++", "Git & GitHub", "VS Code", "Figma"],
    },
    {
      title: "Others",
      skills: ["REST API", "Responsive Design", "Web Accessibility", "Problem Solving"],
    },
  ];

  return (
    <motion.div
      className="min-h-screen px-6 py-10 bg-[#0a0a0a] text-white"
      initial={{ opacity: 0, y: 40 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 1 }}
      viewport={{ once: true }}
    >
      <h2 className="text-4xl font-bold text-center mb-10 text-blue-400 underline decoration-blue-500 decoration-2">My Skills</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        {categories.map((category, index) => (
          <motion.div
            key={index}
            className="bg-[#121212] rounded-lg shadow-lg p-6 border border-blue-900"
            whileHover={{ scale: 1.03 }}
            transition={{ duration: 0.3 }}
          >
            <h3 className="text-xl font-semibold text-blue-300 mb-4">{category.title}</h3>
            <ul className="grid grid-cols-2 gap-3">
              {category.skills.map((skill, idx) => (
                <motion.li
                  key={idx}
                  className="bg-blue-800 text-white text-sm px-3 py-2 rounded-md shadow-md hover:bg-blue-600 transition"
                  whileHover={{ scale: 1.1 }}
                >
                  {skill}
                </motion.li>
              ))}
            </ul>
          </motion.div>
        ))}
      </div>
    </motion.div>
  );
};

export default Skills;
