import { motion } from "framer-motion";

const projects = [
  {
    title: "Smart Railway Crack Detection",
    description: "An IoT-based system using Raspberry Pi, Piezo sensors & GPS to detect cracks in real time. Data is displayed on a live dashboard.",
    tech: ["Raspberry Pi", "Python", "Flask", "React", "IoT"],
    link: "https://github.com/your-username/railway-crack-detection",
  },
  {
    title: "AI-Based Health Prediction System",
    description: "Predicts diseases using social media activity integrated with medical appointment booking and doctor video consultations.",
    tech: ["React", "Node.js", "MongoDB", "AI", "Razorpay"],
    link: "https://github.com/your-username/health-predictor",
  },
  {
    title: "Smart Glasses for Visually Impaired",
    description: "Low-cost wearable device using ToF sensors, OCR, and bone conduction to help blind users navigate and read text.",
    tech: ["Raspberry Pi", "Python", "Tesseract OCR", "gTTS"],
    link: "https://github.com/your-username/smart-glasses",
  },
];

const cardVariants = {
  hidden: { opacity: 0, y: 50 },
  visible: (i) => ({
    opacity: 1,
    y: 0,
    transition: {
      delay: i * 0.2,
      duration: 0.7,
    },
  }),
};

const Projects = () => {
  return (
    <section className="min-h-screen px-6 py-10 bg-[#0a0a0a] text-white">
      <motion.h2
        className="text-4xl font-bold text-center mb-12 text-blue-400 underline decoration-blue-500 decoration-2"
        initial={{ opacity: 0, y: -40 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        viewport={{ once: true }}
      >
        My Projects
      </motion.h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-10">
        {projects.map((project, index) => (
          <motion.div
            key={index}
            className="bg-[#121212] border border-blue-900 rounded-lg shadow-lg p-6 hover:shadow-blue-800 transition-shadow duration-300"
            variants={cardVariants}
            initial="hidden"
            whileInView="visible"
            custom={index}
            viewport={{ once: true }}
            whileHover={{ scale: 1.03 }}
          >
            <h3 className="text-2xl font-semibold text-blue-300 mb-3">
              {project.title}
            </h3>
            <p className="text-sm mb-4 text-gray-300">{project.description}</p>
            <div className="flex flex-wrap gap-2 mb-4">
              {project.tech.map((tech, i) => (
                <span
                  key={i}
                  className="bg-blue-700 text-white px-3 py-1 rounded-full text-xs shadow-md"
                >
                  {tech}
                </span>
              ))}
            </div>
            <a
              href={project.link}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-block bg-blue-600 hover:bg-blue-500 text-white px-4 py-2 rounded-md transition"
            >
              View Project
            </a>
          </motion.div>
        ))}
      </div>
    </section>
  );
};

export default Projects;
