import { motion } from "framer-motion";
import Typewriter from "typewriter-effect";

const Intro = () => {
  return (
    <section
      id="intro"
      className="relative h-screen bg-black text-white px-6 flex flex-col md:flex-row items-center justify-center overflow-hidden"
    >
      {/* Background floating blobs */}
      <motion.div
        className="absolute w-72 h-72 opacity-20 rounded-full blur-3xl top-[-100px] left-[-100px] z-0"
        animate={{ scale: [1, 1.4, 1], opacity: [0.2, 0.3, 0.2] }}
        transition={{ repeat: Infinity, duration: 10 }}
      />
      <motion.div
        className="absolute w-96 h-96 opacity-20 rounded-full blur-3xl bottom-[-120px] right-[-120px] z-0"
        animate={{ scale: [1, 1.3, 1], opacity: [0.2, 0.4, 0.2] }}
        transition={{ repeat: Infinity, duration: 12 }}
      />

      {/* Left Section: Text Content */}
      <motion.div
        initial={{ opacity: 0, x: -80 }}
        whileInView={{ opacity: 1, x: 0 }}
        transition={{ duration: 1 }}
        viewport={{ once: true }}
        className="md:w-1/2 z-10 text-center md:text-left space-y-6"
      >
        <h1 className="text-4xl sm:text-6xl font-extrabold drop-shadow-lg">
          Hi, I'm{" "}
          <span className="text-blue-500 animate-pulse">Himanshu Kumar</span>
        </h1>

        <div className="text-xl sm:text-2xl font-medium">
          <Typewriter
            options={{
              strings: [
                "Full Stack Developer",
                "Software Developer",
                "Leadership",
                "Tech Explorer",
                "Open Source Contributor",
              ],
              autoStart: true,
              loop: true,
            }}
          />
        </div>

        <p className="max-w-lg text-gray-300 leading-relaxed text-lg">
          I design and build modern, performant web applications with stunning
          UIs, seamless UX, and clean code. Letâ€™s create something exceptional
          together!
        </p>

        <div className="flex flex-col sm:flex-row gap-4">
          <a
            href="#projects"
            className="px-6 py-3 bg-blue-600 text-white font-semibold rounded-lg shadow-lg transition-all duration-300 hover:shadow-blue-500 hover:scale-105 relative overflow-hidden group"
          >
            <span className="relative z-10">View Projects</span>
            <span className="absolute inset-0 bg-blue-400 blur-xl opacity-30 group-hover:opacity-100 transition-all duration-500"></span>
          </a>

          <a
            href="/assets/Himanshu_Kumar_CV.pdf" // Make sure to place your CV in public/assets
            target="_blank"
            rel="noopener noreferrer"
            className="px-6 py-3 border border-white text-white font-semibold rounded-lg transition-all duration-300 hover:bg-white hover:text-black hover:scale-105 relative overflow-hidden group"
          >
            <span className="relative z-10">Download CV</span>
            <span className="absolute inset-0 bg-white blur-xl opacity-10 group-hover:opacity-40 transition-all duration-500"></span>
          </a>
        </div>
      </motion.div>

      {/* Right Section: Glowing Hexagon Frame with Image */}
      <motion.div
        initial={{ opacity: 0, x: 80 }}
        whileInView={{ opacity: 1, x: 0 }}
        transition={{ duration: 1 }}
        viewport={{ once: true }}
        className="mt-12 md:mt-0 md:w-1/2 flex justify-center z-10"
      >
        <div className="relative w-60 h-60 md:w-80 md:h-80 hexagon-frame">
          <img
            src="/assets/profile.jpg" // your image path here
            alt="Profile"
            className="absolute inset-0 w-full h-full object-cover hexagon-shape"
          />
          <div className="glow-border"></div>
        </div>
      </motion.div>
    </section>
  );
};

export default Intro;
