import { motion } from "framer-motion";

const skills = ["HTML", "CSS", "JavaScript", "React", "Node.js", "MongoDB", "Express", "Python"];

const Skills = () => {
  return (
    <div className="min-h-screen flex flex-col items-center justify-center px-4">
      <h2 className="text-4xl font-bold mb-8">ðŸ’» My Skills</h2>
      <motion.div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
        {skills.map((skill, idx) => (
          <motion.div
            key={idx}
            whileHover={{ scale: 1.1 }}
            className="bg-gray-800 text-white px-6 py-3 rounded-xl text-center shadow hover:shadow-lg transition"
          >
            {skill}
          </motion.div>
        ))}
      </motion.div>
    </div>
  );
};

export default Skills;
