import { useState } from "react";
import { motion } from "framer-motion";
import { Link } from "react-scroll"; // optional if using scroll navigation

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const navLinks = ["Home", "Skills", "Projects", "Achievements", "Contact"];

  return (
    <motion.nav
      initial={{ y: -50, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.6 }}
      className="fixed top-0 w-full z-50 backdrop-blur bg-[#0f172a]/70 border-b border-blue-900 shadow-md"
    >
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        {/* Logo */}
        <motion.h1
          whileHover={{ scale: 1.1 }}
          className="text-white text-2xl font-bold cursor-pointer"
        >
          Himanshu.dev
        </motion.h1>

        {/* Desktop Nav */}
        <ul className="hidden md:flex gap-8 text-white text-lg font-medium">
          {navLinks.map((link, idx) => (
            <motion.li
              key={idx}
              whileHover={{ y: -4, scale: 1.05 }}
              transition={{ type: "spring", stiffness: 300 }}
              className="cursor-pointer hover:text-blue-400 transition duration-200 relative"
            >
              <Link
                to={link.toLowerCase()}
                smooth={true}
                duration={500}
                offset={-80}
              >
                {link}
              </Link>
              <span className="absolute left-0 bottom-0 w-0 h-0.5 bg-blue-500 transition-all duration-300 group-hover:w-full"></span>
            </motion.li>
          ))}
        </ul>

        {/* Hamburger Menu */}
        <div
          className="md:hidden cursor-pointer"
          onClick={() => setIsOpen(!isOpen)}
        >
          <div className="space-y-1">
            <div className="w-6 h-0.5 bg-white"></div>
            <div className="w-6 h-0.5 bg-white"></div>
            <div className="w-6 h-0.5 bg-white"></div>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      {isOpen && (
        <motion.div
          initial={{ height: 0 }}
          animate={{ height: "auto" }}
          exit={{ height: 0 }}
          transition={{ duration: 0.3 }}
          className="md:hidden bg-[#0f172a] px-6 pt-4 pb-6 text-white"
        >
          <ul className="flex flex-col gap-4 text-lg">
            {navLinks.map((link, idx) => (
              <motion.li
                key={idx}
                whileTap={{ scale: 0.95 }}
                className="cursor-pointer hover:text-blue-400"
                onClick={() => setIsOpen(false)}
              >
                <Link
                  to={link.toLowerCase()}
                  smooth={true}
                  duration={500}
                  offset={-80}
                >
                  {link}
                </Link>
              </motion.li>
            ))}
          </ul>
        </motion.div>
      )}
    </motion.nav>
  );
};

export default Navbar;
