import { motion } from "framer-motion";

const About = () => {
  return (
    <section
      id="about"
      className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-b from-black to-gray-900 text-white py-20 px-6"
    >
      <motion.h2
        initial={{ opacity: 0, y: -20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="text-4xl font-bold mb-12 text-center"
      >
        About Me
      </motion.h2>

      <div className="flex flex-col md:flex-row items-center justify-center gap-10 max-w-6xl w-full">
        {/* Left Side: Intro */}
        <motion.div
          initial={{ x: -50, opacity: 0 }}
          whileInView={{ x: 0, opacity: 1 }}
          transition={{ duration: 1 }}
          className="md:w-2/3 w-full text-center md:text-left"
        >
          <p className="text-lg leading-relaxed text-gray-300">
            I'm Himanshu Kumar, a passionate Full Stack Developer with a love for building
            elegant and functional digital experiences. From frontend aesthetics to backend
            logic, I bring projects to life using modern tools and clean code. I enjoy exploring
            new technologies, contributing to open source, and leading impactful software development.
            Let's build something extraordinary together!
          </p>
        </motion.div>

        {/* Right Side: Two Boxes */}
        <motion.div
          initial={{ x: 50, opacity: 0 }}
          whileInView={{ x: 0, opacity: 1 }}
          transition={{ duration: 1 }}
          className="flex flex-col gap-6"
        >
          {/* Co-Founder Section */}
          <div className="bg-blue-900 bg-opacity-50 border border-blue-700 rounded-xl px-6 py-4 text-center shadow-lg w-48">
            <h3 className="text-xl font-semibold text-white">Co-Founder</h3>
          </div>

          {/* Learning Section */}
          <div className="bg-blue-900 bg-opacity-50 border border-blue-700 rounded-xl px-6 py-4 text-center shadow-lg w-48">
            <h3 className="text-xl font-semibold text-white">Learning</h3>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
