import { motion } from "framer-motion";
import Typewriter from "typewriter-effect";

const Intro = () => {
  return (
    <section className="h-screen flex flex-col justify-center items-center bg-gradient-to-br from-[#0f172a] to-[#1e293b] text-white px-6">
      {/* Intro Title */}
      <motion.h1
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
        className="text-4xl sm:text-6xl font-bold text-center mb-4"
      >
        Hi, I'm <span className="text-blue-500">Himanshu Kumar</span>
      </motion.h1>

      {/* Typewriter Effect */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1, duration: 1 }}
        className="text-xl sm:text-2xl font-medium text-center mb-6"
      >
        <Typewriter
          options={{
            strings: [
              "Full Stack Developer",
              "UI/UX Enthusiast",
              "Tech Explorer",
              "Open Source Contributor",
            ],
            autoStart: true,
            loop: true,
          }}
        />
      </motion.div>

      {/* Description */}
      <motion.p
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 1.8, duration: 1 }}
        className="max-w-xl text-center text-gray-300 mb-8"
      >
        I design and build modern web applications with performance, user experience, and clean code in mind. Letâ€™s build something amazing together!
      </motion.p>

      {/* Buttons */}
      <motion.div
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ delay: 2.2, duration: 0.5 }}
        className="flex gap-4"
      >
        <a
          href="#projects"
          className="px-6 py-3 bg-blue-600 hover:bg-blue-700 transition-all duration-300 rounded-md text-white font-medium"
        >
          View Projects
        </a>
        <a
          href="#contact"
          className="px-6 py-3 border border-white hover:bg-white hover:text-black transition-all duration-300 rounded-md text-white font-medium"
        >
          Contact Me
        </a>
      </motion.div>
    </section>
  );
};

export default Intro;



























