import { motion } from "framer-motion";

const Intro = () => {
  return (
    <motion.div
      className="min-h-screen flex flex-col justify-center items-center px-4"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
    >
      <motion.h1
        className="text-5xl font-bold mb-4 text-center"
        initial={{ y: -50 }}
        animate={{ y: 0 }}
        transition={{ duration: 0.5 }}
      >
        Hi, I'm Himanshu ðŸ‘‹
      </motion.h1>
      <motion.p
        className="text-xl text-center max-w-xl"
        initial={{ y: 50 }}
        animate={{ y: 0 }}
        transition={{ duration: 0.5 }}
      >
        A passionate full-stack developer focused on building modern, responsive, and efficient web applications.
      </motion.p>
    </motion.div>
  );
};

export default Intro;
