import { motion } from "framer-motion";
import Typewriter from "typewriter-effect";

const Intro = () => {
  return (
    <section className="relative h-screen flex flex-col justify-center items-center bg-black text-white px-6 overflow-hidden">

      {/* Floating Blobs */}
      <motion.div
        className="absolute w-72 h-72 bg-blue-500 opacity-30 rounded-full blur-3xl top-[-100px] left-[-100px] z-0"
        animate={{ scale: [1, 1.2, 1], opacity: [0.3, 0.4, 0.3] }}
        transition={{ repeat: Infinity, duration: 10 }}
      />
      <motion.div
        className="absolute w-96 h-96 opacity-20 rounded-full blur-3xl bottom-[-120px] right-[-120px] z-0"
        animate={{ scale: [1, 1.3, 1], opacity: [0.2, 0.3, 0.2] }}
        transition={{ repeat: Infinity, duration: 12 }}
      />

      {/* Title */}
      <motion.h1
        initial={{ opacity: 0, y: -60 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
        className="text-4xl sm:text-6xl font-extrabold text-center mb-4 z-10"
      >
        Hi, I'm <span className="text-blue-500 drop-shadow-lg">Himanshu Kumar</span>
      </motion.h1>

      {/* Typewriter */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1, duration: 1 }}
        className="text-xl sm:text-2xl font-medium text-center mb-6 z-10"
      >
        <Typewriter
          options={{
            strings: [
              "Full Stack Developer 💻",
              "UI/UX Enthusiast 🎨",
              "Tech Explorer 🚀",
              "Open Source Contributor 🌐",
            ],
            autoStart: true,
            loop: true,
          }}
        />
      </motion.div>

      {/* Description */}
      <motion.p
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 1.8, duration: 1 }}
        className="max-w-2xl text-center text-gray-300 mb-8 leading-relaxed z-10"
      >
        I design and build modern, performant web applications with stunning UIs,
        seamless UX, and clean code. Let’s create something exceptional together!
      </motion.p>

      {/* Buttons */}
      <motion.div
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ delay: 2.2, duration: 0.6 }}
        className="flex gap-6 z-10"
      >
        <a
          href="#projects"
          className="px-6 py-3 bg-blue-600 hover:bg-blue-700 transition-all duration-300 rounded-lg text-white font-semibold shadow-lg"
        >
          View Projects
        </a>
        <a
          href="#contact"
          className="px-6 py-3 border border-white hover:bg-white hover:text-black transition-all duration-300 rounded-lg text-white font-semibold"
        >
          Contact Me
        </a>
      </motion.div>
    </section>
  );
};

export default Intro;



























