// components/Contact.jsx
import { motion } from "framer-motion";

const Contact = () => {
  return (
    <section className="min-h-screen px-6 py-20 bg-dark text-white">
      <motion.h2
        className="text-4xl font-bold text-center mb-10"
        initial={{ opacity: 0, scale: 0.8 }}
        whileInView={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.8 }}
      >
        Contact Me
      </motion.h2>

      <motion.form
        className="max-w-xl mx-auto space-y-6"
        initial={{ opacity: 0, y: 50 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        <div>
          <label className="block mb-2">Name</label>
          <input
            type="text"
            className="w-full p-3 rounded bg-gray-800 text-white border border-blue-500"
            placeholder="Your Name"
          />
        </div>
        <div>
          <label className="block mb-2">Email</label>
          <input
            type="email"
            className="w-full p-3 rounded bg-gray-800 text-white border border-blue-500"
            placeholder="Your Email"
          />
        </div>
        <div>
          <label className="block mb-2">Message</label>
          <textarea
            rows="5"
            className="w-full p-3 rounded bg-gray-800 text-white border border-blue-500"
            placeholder="Your Message"
          />
        </div>
        <motion.button
          type="submit"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          className="bg-blue-500 hover:bg-blue-600 transition-colors px-6 py-3 rounded font-semibold text-white w-full"
        >
          Send Message
        </motion.button>
      </motion.form>
    </section>
  );
};

export default Contact;
