import { motion } from "framer-motion";

const projects = [
  { name: "Portfolio Website", desc: "Animated portfolio using React & Tailwind" },
  { name: "Medical Booking App", desc: "Appointment & video consultation platform" },
  { name: "Smart Glasses System", desc: "Assistance for visually impaired" },
];

const Projects = () => {
  return (
    <div className="min-h-screen flex flex-col items-center justify-center px-4">
      <h2 className="text-4xl font-bold mb-8">ðŸš€ My Projects</h2>
      <div className="space-y-6 w-full max-w-3xl">
        {projects.map((project, idx) => (
          <motion.div
            key={idx}
            className="bg-gray-800 rounded-xl p-6 hover:shadow-lg transition"
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: idx * 0.2 }}
          >
            <h3 className="text-2xl font-semibold">{project.name}</h3>
            <p className="mt-2 text-gray-300">{project.desc}</p>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default Projects;
